(32, 1, '2023-02-11 21:18:27', '2023-02-11 19:18:27', '<!-- wp:paragraph -->\n<p>Use the contact form below to contact with us.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:getwid/section {\"align\":\"wide\",\"skipLayout\":true,\"backgroundColor\":\"base\"} -->\n<div class=\"wp-block-getwid-section alignwide\"><div class=\"wp-block-getwid-section__wrapper\"><div class=\"wp-block-getwid-section__inner-wrapper\"><div class=\"wp-block-getwid-section__background-holder\"><div class=\"wp-block-getwid-section__background has-background has-base-background-color\"></div><div class=\"wp-block-getwid-section__foreground\"></div></div><div class=\"wp-block-getwid-section__content\"><div class=\"wp-block-getwid-section__inner-content\"><!-- wp:getwid/contact-form {\"textColor\":\"contrast\"} -->\n<!-- wp:getwid/field-name {\"required\":true} /-->\n\n<!-- wp:getwid/field-email {\"required\":true} /-->\n\n<!-- wp:getwid/field-textarea {\"required\":true} /-->\n<!-- /wp:getwid/contact-form --></div></div></div></div></div>\n<!-- /wp:getwid/section -->', 'Contacts', '', 'publish', 'closed', 'closed', '', 'contacts', '', '', '2023-02-19 14:01:44', '2023-02-19 12:01:44', '', 0, 'http://localhost/Projects/Wordpress/wordpress-23/?page_id=32', 0, 'page', '', 1),
